import {
  Component,
  Input,
  Output,
  EventEmitter,
  OnInit,
  OnChanges,
  SimpleChanges,
} from '@angular/core';
import {
  DynamicFormSection,
  FormService,
  ExtendedFormGroup,
} from 'dynamic-form';
import { IFormAction } from 'projects/dynamic-form/src/lib';

@Component({
  selector: 'storybook-dynamic-form',
  templateUrl: './demo.component.html',
  styleUrls: ['./demo.scss'],
})
export class DemoComponent implements OnInit, OnChanges {
  extendedFormGroup: ExtendedFormGroup;
  constructor(private formService: FormService) {}

  ngOnInit(): void {
    this.extendedFormGroup = this.formService.dynamicFormSectionsToFormGroup([
      this.formSection[this.activeStep],
    ]);
  }

  ngOnChanges(changes: SimpleChanges): void {
    // if (changes.formSection.currentValue) {
    //   this.extendedFormGroup = this.formService.dynamicFormSectionsToFormGroup(
    //     this.formSection
    //   );
    // }
  }

  @Input()
  activeStep: number;

  @Input()
  formSection: DynamicFormSection[];

  @Input()
  hasFormActions: boolean;

  @Input()
  nextAction: IFormAction = { hasAction: false };

  @Input()
  previousAction: IFormAction = { hasAction: false };

  @Input()
  cancelAction: IFormAction = { hasAction: false };

  goToNextStep(): void {
    this.activeStep += 1;
    this.extendedFormGroup = this.formService.dynamicFormSectionsToFormGroup(
      [this.formSection[this.activeStep]],
      this.extendedFormGroup
    );
  }

  gotToPreviousStep(): void {
    console.log('previous', this.extendedFormGroup);
    this.activeStep -= 1;
  }
}

import {
  ChangeDetectorRef,
  Component,
  EventEmitter,
  Input,
  Output,
  TemplateRef,
} from '@angular/core';
import { FormControl } from '@angular/forms';
import { KeyValue } from '@angular/common';
import { DynamicFormItem, DynamicFormOption } from './model/models';
import {
  ExtendedFormGroup,
  ExtendedFormGroupControls,
} from './model/custom-classes/extended-form-group';
import { ExtendedFormControl } from './model/custom-classes/extended-form-control';
import { ExtendedFormArray } from './model/custom-classes/extended-form-array';
import { ExtendedFormGroupArray } from './model/custom-classes/extended-form-group-array';
import { FormService } from './form.service';

interface IDynamicFormValidationError {
  key: string;
  errorMessage: string;
  sectionId: string;
  sectionIndex: number;
  formItem: Partial<DynamicFormItem>;
}

export interface IFormAction {
  hasAction: boolean;
  label?: string;
}
