import { FormArray, FormControl } from "@angular/forms";
import { DynamicFormItem } from "../../model/dynamicFormItem";

export class ExtendedFormArray extends FormArray {
    formItem: DynamicFormItem;

    constructor(formItem: DynamicFormItem, controls: Array<FormControl>) {
        super(controls);
        this.formItem = formItem;
    }
}

import { FormControl, AsyncValidatorFn, ValidatorFn } from "@angular/forms";
import { DynamicFormItem } from "../../model/dynamicFormItem";
import { ExtendedFormGroup } from "./extended-form-group";
import { ExtendedFormGroupArray } from "./extended-form-group-array";

export class ExtendedFormControl extends FormControl {
    formItem: DynamicFormItem;
    options: DynamicFormItem["options"] = [];
    formGroup: ExtendedFormGroup | ExtendedFormGroupArray;

    constructor(
        formItem: DynamicFormItem,
        value: any,
        validator?: ValidatorFn | ValidatorFn[] | AsyncValidatorFn | AsyncValidatorFn[] | null
    ) {
        super(value, validator);
        this.formItem = formItem;
        this.options = formItem.options ? formItem.options : null;
    }
}

import {
  FormGroup,
  ValidatorFn,
  AbstractControlOptions,
  AsyncValidatorFn,
} from '@angular/forms';
import { DynamicFormSection } from '../../model/dynamicFormSection';
import { ExtendedFormControl } from './extended-form-control';
import { ExtendedFormArray } from './extended-form-array';
import { ExtendedFormGroupArray } from './extended-form-group-array';
export interface ExtendedFormGroupControls {
  [key: string]: ExtendedFormGroupControl | ExtendedFormGroup;
}

export type ExtendedFormGroupControl =
  | ExtendedFormControl
  | ExtendedFormArray
  | ExtendedFormGroup
  | ExtendedFormGroupArray;

export class ExtendedFormGroup extends FormGroup {
  sectionItem: DynamicFormSection;

  constructor(
    controls: ExtendedFormGroupControls,
    item?: DynamicFormSection,
    validatorOrOpts?: ValidatorFn | ValidatorFn[] | AbstractControlOptions,
    asyncValidator?: AsyncValidatorFn | AsyncValidatorFn[]
  ) {
    super(controls, validatorOrOpts, asyncValidator);
    this.sectionItem = item;
  }

  get(path: string | (string | number)[]): ExtendedFormGroupControl {
    return super.get(path) as ExtendedFormGroupControl;
  }
}

import { FormArray } from "@angular/forms";
import { DynamicFormSection } from "../../model/dynamicFormSection";
import { ExtendedFormGroup } from "./extended-form-group";

export class ExtendedFormGroupArray extends FormArray {
    sectionItem: DynamicFormSection;

    constructor(controls: Array<ExtendedFormGroup>, item?: DynamicFormSection) {
        super(controls);
        this.sectionItem = item;
    }
}

