export class ExtendedFormControl extends FormControl {
    formItem: DynamicFormItem;
    options: DynamicFormItem["options"] = [];
    formGroup: ExtendedFormGroup | ExtendedFormGroupArray;

    constructor(
        formItem: DynamicFormItem,
